import React, { useState } from 'react';
import './SplitWithFriends.css'; // Add styles specific to this component

const SplitWithFriends = () => {
  const [friends, setFriends] = useState([]);
  const [expense, setExpense] = useState(0);
  const [contribution, setContribution] = useState({});
  
  const handleExpenseChange = (e) => {
    setExpense(e.target.value);
  };

  const handleFriendChange = (e) => {
    const friend = e.target.value;
    setFriends((prevFriends) => {
      if (!prevFriends.includes(friend)) {
        return [...prevFriends, friend];
      }
      return prevFriends;
    });
  };

  const handleContributionChange = (friend, value) => {
    setContribution((prevContribution) => ({
      ...prevContribution,
      [friend]: value,
    }));
  };

  const handleSubmit = () => {
    const totalContribution = Object.values(contribution).reduce((acc, val) => acc + parseFloat(val), 0);
    if (totalContribution === expense) {
      alert('Expense split successful!');
    } else {
      alert('Total contributions do not match the expense amount.');
    }
  };

  return (
    <div className="split-with-friends-container">
      <h2>Split with Friends</h2>
      <div className="expense-details">
        <label htmlFor="expense">Total Expense: </label>
        <input
          type="number"
          id="expense"
          value={expense}
          onChange={handleExpenseChange}
          placeholder="Enter total expense"
        />
      </div>

      <div className="friends-list">
        <h3>Add Friends</h3>
        <input
          type="text"
          placeholder="Enter friend's name"
          onBlur={handleFriendChange}
        />
        <ul>
          {friends.map((friend, index) => (
            <li key={index}>
              <label>{friend}</label>
              <input
                type="number"
                placeholder="Contribution"
                value={contribution[friend] || ''}
                onChange={(e) => handleContributionChange(friend, e.target.value)}
              />
            </li>
          ))}
        </ul>
      </div>

      <div className="submit-btn">
        <button onClick={handleSubmit}>Submit</button>
      </div>
    </div>
  );
};

export default SplitWithFriends;
