import React from 'react';
import { QrReader } from 'react-qr-reader';

const QRScanner = () => {
  const handleResult = (result, error) => {
    if (result) {
      console.log('Scanned Result:', result.text);
      alert(`Scanned Result: ${result.text}`); // Display scanned result
    }

    if (error) {
      console.error(error);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Scan QR Code</h2>
      <div style={styles.scannerContainer}>
        <QrReader
          onResult={handleResult}
          constraints={{ facingMode: 'environment' }} // Use rear camera
          style={styles.scanner}
        />
      </div>
      <p style={styles.instructions}>Align the QR code within the frame to scan.</p>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    textAlign: 'center',
    backgroundColor: '#f0f4f8',
    minHeight: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  heading: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    color: '#1a85e6',
  },
  scannerContainer: {
    width: '320px',
    height: '320px',
    margin: '0 auto',
    backgroundColor: 'white',
    borderRadius: '16px',
    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.2)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  scanner: {
    width: '100%',
    height: '100%',
    borderRadius: '16px',
  },
  instructions: {
    marginTop: '20px',
    fontSize: '16px',
    color: '#555',
    maxWidth: '300px',
    lineHeight: '1.5',
  },
};

export default QRScanner;
