import React from 'react';
import { QrReader } from 'react-qr-reader';

const QRScanner = () => {
  const handleResult = (result, error) => {
    if (result) {
      console.log('Scanned Result:', result.text);
      alert(`Scanned Result: ${result.text}`); // Display scanned result
    }

    if (error) {
      console.error(error);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Scan QR Code</h2>
      <div style={styles.scannerContainer}>
        <QrReader
          onResult={handleResult}
          constraints={{ facingMode: 'environment' }} // Use rear camera
          style={styles.scanner}
        />
      </div>
      <p style={styles.instructions}>Align the QR code within the frame to scan.</p>
    </div>
  );
};

export default QRScanner;
