import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SplitExpenses.css'; // Add styles specific to this component
import SplitWithFriends from '../../assets/images/SplitWithFriends.png';
import SplitCustom from '../../assets/images/SplitCustom.png';
import CategorizedSplit from '../../assets/images/CategorizedSplit.png';
import RecurringSplit from '../../assets/images/RecurringSplit.png';
import EventSplit from '../../assets/images/EventSplit.png';

const SplitExpenses = () => {
  const navigate = useNavigate();

  // State to track total expense, friends list, and custom amounts
  const [totalExpense, setTotalExpense] = useState('');
  const [friends, setFriends] = useState([{ name: '', amount: '' }]);
  const [splitType, setSplitType] = useState('equal'); // 'equal' or 'custom'

  // Function to handle navigation on clicking any option
  const handleNavigation = (path) => {
    navigate(path);
  };

  // Handle adding new friends
  const addFriend = () => {
    setFriends([...friends, { name: '', amount: '' }]);
  };

  // Handle input for total expense
  const handleExpenseChange = (e) => {
    setTotalExpense(e.target.value);
  };

  // Handle friend name input
  const handleFriendNameChange = (index, value) => {
    const updatedFriends = [...friends];
    updatedFriends[index].name = value;
    setFriends(updatedFriends);
  };

  // Handle custom amount input for each friend
  const handleAmountChange = (index, value) => {
    const updatedFriends = [...friends];
    updatedFriends[index].amount = value;
    setFriends(updatedFriends);
  };

  // Calculate the equal split
  const calculateEqualSplit = () => {
    if (friends.length > 0 && totalExpense > 0) {
      const amount = (totalExpense / friends.length).toFixed(2);
      return amount;
    }
    return '';
  };

  // Handle split type change
  const handleSplitTypeChange = (type) => {
    setSplitType(type);
  };

  return (
    <div className="split-expenses-container">
      <h2>Split Expenses</h2>
      <div className="split-icons">
        {/* Split Among Friends/Contacts */}
        <div className="icon" onClick={() => handleNavigation('/splitwithfriends')}>
          <img src={SplitWithFriends} alt="Split with Friends" />
          <p>Split with Friends</p>
        </div>

        {/* Split Custom */}
        <div className="icon" onClick={() => handleNavigation('/splitcustom')}>
          <img src={SplitCustom} alt="Split Custom" />
          <p>Split Custom</p>
        </div>

        {/* Split Recurring Expenses */}
        <div className="icon" onClick={() => handleNavigation('/splitrecurring')}>
          <img src={RecurringSplit} alt="Recurring Expenses" />
          <p>Split Recurring Expenses</p>
        </div>

        {/* Split by Categories */}
        <div className="icon" onClick={() => handleNavigation('/splitbycategory')}>
          <img src={CategorizedSplit} alt="Split by Category" />
          <p>Split by Categories</p>
        </div>

        {/* Event Splitting */}
        <div className="icon" onClick={() => handleNavigation('/eventsplitting')}>
          <img src={EventSplit} alt="Event Splitting" />
          <p>Event Splitting</p>
        </div>
      </div>

      {/* Expense and Friend Input Section */}
      <div className="expense-input-container">
        <input 
          type="number" 
          placeholder="Enter Total Expense" 
          value={totalExpense} 
          onChange={handleExpenseChange}
        />
        <button onClick={addFriend}>Add Friend</button>

        <div className="friends-list">
          {friends.map((friend, index) => (
            <div key={index} className="friend-entry">
              <input
                type="text"
                placeholder="Friend's Name"
                value={friend.name}
                onChange={(e) => handleFriendNameChange(index, e.target.value)}
              />
              {splitType === 'equal' ? (
                <input
                  type="text"
                  value={calculateEqualSplit()}
                  disabled
                />
              ) : (
                <input
                  type="number"
                  placeholder="Amount"
                  value={friend.amount}
                  onChange={(e) => handleAmountChange(index, e.target.value)}
                />
              )}
            </div>
          ))}
        </div>

        {/* Split Type Options */}
        <div className="split-type">
          <label>
            <input 
              type="radio" 
              name="split-type" 
              value="equal" 
              checked={splitType === 'equal'} 
              onChange={() => handleSplitTypeChange('equal')}
            />
            Equal Split
          </label>
          <label>
            <input 
              type="radio" 
              name="split-type" 
              value="custom" 
              checked={splitType === 'custom'} 
              onChange={() => handleSplitTypeChange('custom')}
            />
            Custom Split
          </label>
        </div>
      </div>
    </div>
  );
};

export default SplitExpenses;
