import React, { useState } from "react";
import "./SplitWithFriends.css"; // Link to your CSS file

const SplitExpenses = () => {
  const [totalExpense, setTotalExpense] = useState("");
  const [friends, setFriends] = useState([]);
  const [contributions, setContributions] = useState([]);
  const [qrCodes, setQrCodes] = useState([]);

  // Handle Total Expense input
  const handleExpenseChange = (e) => {
    setTotalExpense(e.target.value);
  };

  // Add a friend by searching their name
  const addFriend = () => {
    const friendName = prompt("Enter friend's name:");
    const friendPhone = prompt("Enter friend's phone number:");

    if (friendName && friendPhone) {
      setFriends([...friends, { name: friendName, phone: friendPhone }]);
    }
  };

  // Set individual contributions
  const handleContributionChange = (amount, index) => {
    const updatedContributions = [...contributions];
    updatedContributions[index] = amount;
    setContributions(updatedContributions);
  };

  // Calculate and Generate QR Codes
  const handleSubmit = () => {
    if (friends.length === 0 || !totalExpense) {
      alert("Please add friends and enter a total expense.");
      return;
    }

    const total = parseFloat(totalExpense);
    const numFriends = friends.length;

    if (!isNaN(total) && contributions.length === numFriends) {
      // Generate QR codes for each friend showing their contribution
      const qrImages = friends.map((friend, index) => ({
        name: friend.name,
        phone: friend.phone,
        qrCode: `https://api.qrserver.com/v1/create-qr-code/?data=Contribution%3A%20${contributions[index]}&size=100x100`,
      }));
      setQrCodes(qrImages);

      // Optionally send QR codes to each friend
      qrImages.forEach((qr) => {
        // Replace with SMS API to send the QR code
        console.log(`Sending QR to ${qr.phone}: ${qr.qrCode}`);
      });
    }
  };

  return (
    <div className="split-expenses-container">
      <h2>Split Expenses</h2>
      <div className="expense-input-container">
        {/* Total Expense Input */}
        <input
          type="number"
          value={totalExpense}
          onChange={handleExpenseChange}
          placeholder="Enter total expense"
        />

        {/* Friends List Input */}
        <div className="friends-container">
          {friends.map((friend, index) => (
            <div key={index} className="friend-entry">
              <p>{friend.name} ({friend.phone})</p>
              <input
                type="number"
                placeholder="Enter contribution"
                value={contributions[index] || ""}
                onChange={(e) => handleContributionChange(e.target.value, index)}
              />
            </div>
          ))}
        </div>

        {/* Button to add more friends */}
        <button onClick={addFriend} className="submit-button">Add Friend</button>

        {/* Submit Button */}
        <button onClick={handleSubmit} className="submit-button">
          Generate QR Codes
        </button>
      </div>

      {/* QR Code and Contribution Display */}
      {qrCodes.length > 0 && (
        <div className="qr-container">
          {qrCodes.map((qr, index) => (
            <div key={index} className="qr-box">
              <p>{qr.name} ({qr.phone})</p>
              <p>Contribution: {contributions[index]}</p>
              <img src={qr.qrCode} alt="QR Code" />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SplitExpenses;
