import React, { useState } from 'react';
import './Split.css';  // Link to your CSS file

const SplitExpenses = () => {
  const [totalExpense, setTotalExpense] = useState('');
  const [friends, setFriends] = useState(['']); // Stores friend names
  const [contributions, setContributions] = useState([]);
  const [qrCodes, setQrCodes] = useState([]);

  // Handle Total Expense input
  const handleExpenseChange = (e) => {
    setTotalExpense(e.target.value);
  };

  // Handle adding/removing friend input
  const handleFriendChange = (e, index) => {
    const updatedFriends = [...friends];
    updatedFriends[index] = e.target.value;
    setFriends(updatedFriends);
  };

  // Add a friend input
  const addFriend = () => {
    if (friends.length < 5) {
      setFriends([...friends, '']);
    }
  };

  // Remove a friend input
  const removeFriend = (index) => {
    const updatedFriends = friends.filter((_, i) => i !== index);
    setFriends(updatedFriends);
  };

  // Calculate the equal split and custom contributions
  const handleSubmit = () => {
    const total = parseFloat(totalExpense);
    const numFriends = friends.length;

    if (numFriends > 0 && !isNaN(total)) {
      // Split equally
      const equalShare = total / (numFriends + 1);  // Total divided by number of people (including the user)

      // Create contributions for each friend
      const updatedContributions = friends.map(() => equalShare);
      setContributions(updatedContributions);

      // Generate QR codes for each friend showing their contribution
      const qrImages = friends.map((friend, index) => ({
        name: friend,
        qrCode: `https://api.qrserver.com/v1/create-qr-code/?data=Contribution%3A%20${updatedContributions[index]}&size=100x100`,
      }));
      setQrCodes(qrImages);
    }
  };

  return (
    <div className="split-expenses-container">
      <h2>Split Expenses</h2>
      <div className="expense-input-container">
        {/* Total Expense Input */}
        <input
          type="number"
          value={totalExpense}
          onChange={handleExpenseChange}
          placeholder="Enter total expense"
        />

        {/* Friends List Input */}
        <div className="friends-container">
          {friends.map((friend, index) => (
            <div key={index} className="friend-entry">
              <input
                type="text"
                value={friend}
                onChange={(e) => handleFriendChange(e, index)}
                placeholder={`Friend ${index + 1} Name`}
              />
              {index > 0 && (
                <button onClick={() => removeFriend(index)} className="remove-button">Remove</button>
              )}
            </div>
          ))}
        </div>

        {/* Button to add more friends */}
        {friends.length < 5 && (
          <button onClick={addFriend} className="submit-button">Add Friend</button>
        )}

        {/* Submit Button */}
        <button onClick={handleSubmit} className="submit-button">
          Submit
        </button>
      </div>

      {/* QR Code and Contribution Display */}
      {qrCodes.length > 0 && (
        <div className="qr-container">
          {qrCodes.map((qr, index) => (
            <div key={index} className="qr-box">
              <p>{qr.name}</p>
              <p>Contribution: {contributions[index]}</p>
              <img src={qr.qrCode} alt="QR Code" />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SplitExpenses;
