import React from 'react';
import './OtpVerification.css';

const OtpVerification = () => {
    return (
        <div className="otp-section">
            <div className="otp-container">
                <h2>OTP Verification</h2>
                <p>Please enter the OTP sent to your registered phone number.</p>
                <input type="text" className="otp-input" placeholder="Enter OTP" />
                <button className="verify-btn">Verify OTP</button>
                <p className="resend-link">Didn't receive OTP? Resend</p>
            </div>
        </div>
    );
};

export default OtpVerification;
