import React from 'react';
import { QrReader } from 'react-qr-reader';
import { useNavigate } from 'react-router-dom';  // Import useNavigate hook

const QRScanner = ({ onScanComplete }) => {
  const navigate = useNavigate();  // Initialize navigate function

  const handleResult = (result, error) => {
    if (result) {
      console.log('Scanned Result:', result.text);
      onScanComplete();  // Call the function passed as a prop
    }
    if (error) {
      console.error(error);
    }
  };

  // Function to handle the navigation back to MoneyTransfer page
  const handleBackToMoneyTransfer = () => {
    navigate('/money-transfer');  // Navigate to MoneyTransfer page when button is clicked
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>Scan QR Code</h2>
      <div style={{ width: '300px', margin: '0 auto' }}>
        <QrReader
          onResult={handleResult}
          style={{ width: '100%' }}
        />
      </div>
      <p>Align the QR code within the frame to scan.</p>

      {/* Back to Money Transfer Button */}
      <button 
        onClick={handleBackToMoneyTransfer} 
        style={{
          marginTop: '20px', 
          padding: '10px 20px', 
          fontSize: '16px',
          backgroundColor: '#33e6f6',  // Custom background color
          border: 'none', 
          borderRadius: '5px', 
          cursor: 'pointer'
        }}
      >
        Back to Money Transfer
      </button>
    </div>
  );
};

export default QRScanner;
