import React, { useState } from 'react';
import './Carousel.css';
import firebase from '../../firebase';
import logo1 from '../../assets/images/logo1.png';


const Carousel = () => {
    const [mobile, setMobile] = useState('');
    const [showres, setShowres] = useState(false);
    const [showerror, setShowerror] = useState(false);

    const handleChange = (e) => {
        const { value } = e.target;
        setMobile(value);
    };

    const configureCaptcha = () => {
        window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('sign-in-button', {
            size: 'invisible',
            callback: (response) => {
                onSignInSubmit();
            },
            defaultCountry: 'IN',
        });
    };

    const onSignInSubmit = (e) => {
        e.preventDefault();
        configureCaptcha();
        const phoneNumber = "+91" + mobile;
        const appVerifier = window.recaptchaVerifier;

        firebase
            .auth()
            .signInWithPhoneNumber(phoneNumber, appVerifier)
            .then((confirmationResult) => {
                window.confirmationResult = confirmationResult;
                console.log('OTP sent successfully');
                setShowres(true);
            })
            .catch((error) => {
                console.error(error);
                setShowerror(true);
            });
    };

    const showStyle = { display: 'block', color: '#fff' };
    const closeStyle = { display: 'none', color: '#fff' };

    return (
        <div className="section mt-sm-5">
            <div className="container-fluid">
                <div className="row">
                    {/* Carousel Section */}
                    <div className="section1 col-lg-6">
                        <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
                            <ol className="carousel-indicators">
                                <li data-target="#carouselExampleIndicators" data-slide-to="0" className="active"></li>
                                <li data-target="#carouselExampleIndicators" data-slide-to="1"></li>
                                <li data-target="#carouselExampleIndicators" data-slide-to="2"></li>
                            </ol>
                            <div className="carousel-inner" role="listbox">
                                <div className="carousel-item " style={{ textAlign: 'left' }}>
                                    <h2>Manage Your Expenses with <span>SaathPay</span></h2>
                                    <p>Split bills easily, track group expenses, and settle payments effortlessly.</p>
                                </div>
                                <div className="carousel-item" style={{ textAlign: 'left' }}>
                                    <h2>Keep Track of Group <span>Expenses</span></h2>
                                    <p>Manage shared expenses with friends, roommates, or colleagues all in one place.</p>
                                </div>
                                <div className="carousel-item" style={{ textAlign: 'left' }}>
                                    <h2>Settle Payments <span>Hassle-Free</span></h2>
                                    <p>Send and receive payments directly while keeping track of who owes whom.</p>
                                </div>
                            </div>
                        </div>
                        {/* Phone Number Form */}
                        <div className="form_area" id="myDIV">
                            <form className="d-flex" id="contactForm" onSubmit={onSignInSubmit}>
                                <div id="sign-in-button"></div>
                                <input
                                    type="number"
                                    className="form-control number"
                                    placeholder="Phone number"
                                    name="mobile"
                                    pattern="[6789][0-9]{9}"
                                    onChange={handleChange}
                                    required
                                />
                                <button type="submit" className="btn btn-primary nav_btn">
                                    Get Started
                                </button>
                            </form>
                        </div>
                        {/* Confirmation Message */}
                        <div style={showres ? showStyle : closeStyle} className="form_area" id="my2">
                            <div className="d-flex">
                                {!showerror ? (
                                    <p>
                                        Thank You üòÅ. We'll notify you when your account is set up and ready to track expenses
                                        with SaathPay.
                                    </p>
                                ) : (
                                    <p>Captcha Already Verified. Please reload to add a new number.</p>
                                )}
                            </div>
                        </div>
                    </div>
                    {/* SVG/Visual Section */}


                </div>
            </div>
        </div>
    );
};

export default Carousel;
