import React, { useState } from 'react';
import './Carousel.css';
import firebase from '../../firebase';

const Carousel = () => {
    const [mobile, setMobile] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showres, setShowres] = useState(false);
    const [showerror, setShowerror] = useState(false);
    const [isLogin, setIsLogin] = useState(true);
    const [usePhone, setUsePhone] = useState(false);

    // Phone Number Login Functions
    const handlePhoneChange = (e) => {
        const { value } = e.target;
        setMobile(value);
    };

    const configureCaptcha = () => {
        window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier('sign-in-button', {
            size: 'invisible',
            callback: (response) => {
                onSignInSubmit();
            },
            defaultCountry: 'IN',
        });
    };

    const onSignInSubmit = (e) => {
        e.preventDefault();
        configureCaptcha();
        const phoneNumber = '+91' + mobile;

        const appVerifier = window.recaptchaVerifier;
        firebase
            .auth()
            .signInWithPhoneNumber(phoneNumber, appVerifier)
            .then((confirmationResult) => {
                window.confirmationResult = confirmationResult;
                setShowres(true);
            })
            .catch((error) => {
                console.log(error);
                setShowerror(true);
            });
    };

    // Email/Password Authentication
    const handleEmailSignUp = async (e) => {
        e.preventDefault();
        try {
            await firebase.auth().createUserWithEmailAndPassword(email, password);
            alert('Sign-Up Successful!');
        } catch (error) {
            setShowerror(true);
            console.log(error.message);
        }
    };

    const handleEmailLogin = async (e) => {
        e.preventDefault();
        try {
            await firebase.auth().signInWithEmailAndPassword(email, password);
            alert('Login Successful!');
        } catch (error) {
            setShowerror(true);
            console.log(error.message);
        }
    };

    // UI Component Return
    return (
        <div className="section mt-sm-5">
            <div className="container-fluid">
                <div className="row">
                    <div className="section1 col-lg-6">
                        {/* Carousel Display */}
                        <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
                            <div className="carousel-inner" role="listbox">
                                <div className="carousel-item active" style={{ textAlign: 'left' }}>
                                    <h2>Welcome to <span>SaathPay</span></h2>
                                    <p>Track expenses, split bills, and manage payments seamlessly!</p>
                                </div>
                            </div>
                        </div>

                        {/* Toggle: Use Phone or Email */}
                        <div>
                            <button className="btn btn-outline-primary" onClick={() => setUsePhone(!usePhone)}>
                                {usePhone ? 'Use Email/Password' : 'Use Phone Number'}
                            </button>
                        </div>

                        {/* Phone Number Section */}
                        {usePhone ? (
                            <div className="form_area" id="phoneLogin">
                                <form className="d-flex" onSubmit={onSignInSubmit}>
                                    <div id="sign-in-button"></div>
                                    <input
                                        type="number"
                                        className="form-control number"
                                        placeholder="Phone number"
                                        onChange={handlePhoneChange}
                                        required
                                    />
                                    <button type="submit" className="btn btn-primary nav_btn">
                                        Get Started
                                    </button>
                                </form>
                                {showres && <p>OTP Sent Successfully! Please verify.</p>}
                                {showerror && <p style={{ color: 'red' }}>Error during phone sign-in!</p>}
                            </div>
                        ) : (
                            // Email Login / Sign-Up Section
                            <div className="form_area" id="emailAuth">
                                <form className="d-flex" onSubmit={isLogin ? handleEmailLogin : handleEmailSignUp}>
                                    <input
                                        type="email"
                                        className="form-control"
                                        placeholder="Email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                    />
                                    <input
                                        type="password"
                                        className="form-control"
                                        placeholder="Password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                    />
                                    <button type="submit" className="btn btn-primary nav_btn">
                                        {isLogin ? 'Login' : 'Sign Up'}
                                    </button>
                                </form>
                                <p
                                    onClick={() => setIsLogin(!isLogin)}
                                    style={{ cursor: 'pointer', color: 'blue', marginTop: '10px' }}
                                >
                                    {isLogin ? 'New user? Sign Up' : 'Already have an account? Login'}
                                </p>
                                {showerror && <p style={{ color: 'red' }}>Error during email authentication!</p>}
                            </div>
                        )}
                    </div>

                    {/* Right Image */}
                    <div className="svg-container col-lg-6">
                        <img
                            src="https://stockpe.tech/img/Landing.png"
                            alt="SaathPay Landing"
                            className="landing_svg"
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Carousel;
